import './App.css';
import { useState, useEffect } from 'react';

function App() {

  const [inputValue, setInput] = useState('')
  const [pendingTask, setPendingTask] = useState([])
  const [inProgress, setInProgress] = useState([])
  const [completed, setCompleted] = useState([])

  useEffect(()=> {
    getPendingTask();
  },[]);

  const getPendingTask = () =>{
    fetch('http://localhost:3001/api/pending/')
    .then((resp) => resp.json())
    .then((resp) => setPendingTask(resp))
    .catch((error) => console.log(error));
  }


  console.log(pendingTask)

  const addTask = () =>{
    fetch('http://localhost:3001/api/addpending', {
      method: "POST",
      headers: {"Content-Type": "application/JSON"},
      body: JSON.stringify(inputValue) 
    })
  }

  return (
    <div className="App">
      Project Management
      <input type="text" onChange={(e)=>setInput(e.target.value)} />
      <button onClick={(e)=>addTask()}>Add a task</button>

      Pending Tasks: {pendingTask.map((val)=>{
        return <div>
          <p>{val.tasks}</p>
          </div>
      })}
    </div>
  );
}

export default App;
