import './App.css';
import { useState, useEffect } from 'react';

function App() {

  const [pendingTask, setPendingTask] = useState([])
  const [inProgress, setInProgress] = useState([])
  const [completed, setCompleted] = useState([])

  useEffect(()=> {
    getPendingTask();
  },[]);

  const getPendingTask = () =>{
    fetch('http://localhost:3001/api/pending/')
    .then((resp) => resp.json())
    .then((resp) => setPendingTask(resp))
    .catch((error) => console.log(error));
  }

  console.log(pendingTask)

  const addTask = () =>{
    setPendingTask([...pendingTask])
  }

  return (
    <div className="App">
      Project Management
      <input type="text" onChange={(e)=>setPendingTask(e.target.value)} />
      <button onClick={(e)=>addTask()}>Add a task</button>

      Pending Tasks: {pendingTask.map((val)=>{
        return <div>
            <h1>{val.id}</h1>
          <p>Price: {val.tasks}</p>
          </div>
      })}
    </div>
  );
}

export default App;
